INSERT INTO `services` (`id`, `title`, `description`, `long_description`, `slug`, `category`, `duration`, `price_range`, `features`, `sub_services`, `materials`, `advantages`, `image`, `is_active`, `sort_order`, `created_at`, `updated_at`) VALUES
(3, 'Réparation des fuites', 'Détection et réparation rapide des infiltrations.', 'Une fuite de toit peut rapidement engendrer des dégâts importants : charpente fragilisée, isolant humide, apparition de moisissures. Nous proposons un service rapide de diagnostic et de réparation, quelle que soit la nature de votre couverture (tuiles, ardoises, zinc). Nos équipes utilisent des techniques modernes pour localiser avec précision la source de la fuite et effectuer une réparation durable.', 'reparation-des-fuites', 'Réparation', '1-2 semaines', '2000-8000€', '[\"- Intervention rapide\",\"- Diagnostic pr\\u00e9cis\",\"- R\\u00e9paration durable\",\"- Toutes toitures\"]', '[\",\"]', '[\"Joints d\\u2019\\u00e9tanch\\u00e9it\\u00e9\",\"Tuiles de remplacement\",\"Zinc\",\"R\\u00e9sine\"]', '[\"- R\\u00e9duction imm\\u00e9diate des infiltrations\",\"- Pr\\u00e9vention des d\\u00e9g\\u00e2ts int\\u00e9rieurs\",\"- Garantie de durabilit\\u00e9\",\"- Respect des mat\\u00e9riaux existants\"]', '/uploads/services/1754240415_réparation de fuite.jpg', 1, 3, '2025-08-01 11:57:04', '2025-08-03 15:00:15'),
(5, 'Démoussage et traitement hydrofuge', 'Élimination des mousses et application de traitement protecteur.', 'Les mousses et lichens retiennent l’humidité, fragilisant les tuiles et réduisant l’efficacité de votre toiture. Nous proposons un service complet de démoussage mécanique ou chimique, suivi de l’application d’un traitement hydrofuge. Ce dernier protège la couverture contre l’eau et la saleté, retardant l’apparition de nouvelles mousses et améliorant la durabilité globale de votre toit.', 'demoussage-et-traitement-hydrofuge', 'Entretien', '1 jour - 1 semaine', '500-3000€', '[\"- D\\u00e9moussage complet\",\"- Produit hydrofuge professionnel\",\"- Pr\\u00e9vention humidit\\u00e9\",\"- Protection longue dur\\u00e9e\"]', '[\"Inspection\", \"Nettoyage\", \"Petites réparations\", \"Conseils\"]', '[\"Produits anti-mousse\",\"Traitement hydrofuge\",\"Brosses, Nettoyeurs\"]', '[\"- \\u00c9tanch\\u00e9it\\u00e9 renforc\\u00e9e\",\"- Retard de la repousse des mousses\",\"- Toiture plus saine et esth\\u00e9tique\",\"- Pr\\u00e9vention des infiltrations\"]', '/uploads/services/1754240626_démousage.jpg', 1, 5, '2025-08-01 11:57:04', '2025-08-03 15:03:46'),
(6, 'Nettoyage de toiture', 'Nettoyage haute pression ou doux pour un toit propre et sain.', 'Le nettoyage de toiture élimine les impuretés, salissures et micro-organismes accumulés au fil du temps. Selon l’état et le type de votre couverture, nous utilisons un nettoyage adapté : basse pression pour préserver l’intégrité des matériaux ou haute pression pour un décrassage en profondeur. Ce service redonne à votre toit un aspect neuf tout en améliorant son étanchéité et sa durée de vie.', 'nettoyage-de-toiture', 'Entretien', '1-3 jours', '1000-8000€', '[\"- Nettoyage adapt\\u00e9 (haute ou basse pression)\",\"- Respect des mat\\u00e9riaux\",\"- S\\u00e9curit\\u00e9 assur\\u00e9e\",\"- R\\u00e9sultat imm\\u00e9diat\"]', '[\";\"]', '[\"Nettoyeurs haute\\/basse pression,\",\"Produits nettoyants doux\"]', '[\"- Am\\u00e9lioration de l\\u2019esth\\u00e9tique\",\"- Pr\\u00e9vention de l\\u2019accumulation de salet\\u00e9\",\"- Dur\\u00e9e de vie accrue des mat\\u00e9riaux\",\"- R\\u00e9sultat visible imm\\u00e9diat\"]', '/uploads/services/1754240785_nettoyage.webp', 1, 6, '2025-08-01 11:57:04', '2025-08-03 15:06:25'),
(10, 'Entretien de toiture', 'Maintenance régulière pour prolonger la durée de vie de votre toit.', 'L’entretien régulier de votre toiture permet d’éviter des réparations coûteuses. Notre service comprend une inspection visuelle complète, le nettoyage des gouttières, le contrôle des tuiles et de la zinguerie ainsi que des recommandations adaptées à l’état de votre couverture. Grâce à un entretien programmé, votre toit conserve son étanchéité et son efficacité énergétique tout au long de l’année.', 'entretien-de-toiture', 'Entretien', '1-3 jours', 'À partir de 300€', '[\"- Inspection annuelle\",\"- Nettoyage complet\",\"- V\\u00e9rification zinguerie\",\"- Pr\\u00e9vention fuites\"]', '[{\"name\":\"Entretien Annuel\",\"description\":\"Inspection compl\\u00e8te et nettoyage\",\"price\":\"\\u00c0 partir de 300\\u20ac\",\"duration\":\"1 jour\"},{\"name\":\"Entretien Bisannuel\",\"description\":\"Entretien deux fois par an\",\"price\":\"\\u00c0 partir de 500\\u20ac\",\"duration\":\"2 jours\"}]', '[\"Brosses\",\"Nettoyeurs basse pression\",\"Outils de contr\\u00f4le\"]', '[\"- Dur\\u00e9e de vie prolong\\u00e9e\",\"- Pr\\u00e9vention des infiltrations\",\"- Meilleure performance \\u00e9nerg\\u00e9tique\",\"- \\u00c9conomie sur les r\\u00e9parations futures\"]', '/uploads/services/1754240526_entretien.webp', 1, 3, '2025-08-01 13:32:48', '2025-08-03 15:02:06'),
(13, 'Installation de toiture', 'L’installation d’une toiture consiste à poser ou remplacer l’ensemble des éléments assurant la couverture d’un bâtiment.', 'L’installation de toiture est une étape cruciale pour assurer la protection et l’efficacité énergétique de votre habitation. Nous proposons des solutions adaptées à chaque type de bâtiment : tuiles, ardoises, zinc ou bac acier. Nos couvreurs qualifiés assurent un travail précis, respectant les normes en vigueur et les délais. Chaque projet inclut une étude personnalisée afin de garantir une étanchéité optimale et une finition esthétique durable.', 'installation-de-toiture', 'Installation', '2-7', '00', '[\"- \\u00c9tude personnalis\\u00e9e\",\"- Mat\\u00e9riaux de qualit\\u00e9\",\"- Garantie d\\u00e9cennale\",\"- Respect des d\\u00e9lais\"]', '[\",\"]', '[\"-Tuiles (terre cuite, b\\u00e9ton)\",\"-Ardoises\",\"-Zinc\",\"-Bac acier\"]', '[\"- Protection optimale contre les intemp\\u00e9ries\",\"- Esth\\u00e9tique durable\",\"- Excellente isolation\",\"- Valorisation du patrimoine\"]', '/uploads/services/1754239886_installation toiture.jpg', 1, 0, '2025-08-03 14:44:00', '2025-08-03 14:56:58');

